import React from "react";
import { Link } from "react-router-dom";
// import './css/ProductStyle.css'
import './css/singleProduct.css'
//import products from './ProductsArray';
export default function Product(props){
    // const productlist=[products];
    const {productName,productImage,productPrice}=props
    return(
    //     <div>
    //         <div classNameName='productsDArr'> 
    //                     <div  classNameName="productD-card">
    //      <div classNameName="logoD-cart">
    //         <p>MATALAN</p>
    //     </div> 
    //     <div classNameName="content1">
    //     <div classNameName="main-images">
    //        <Link to={`/product/${productName}`}> <img id="blue" classNameName="blue active"
    //             src={productImage}
    //             alt="blue" /></Link>
    //     </div>
    //    </div>
    //    <div classNameName="content2D">
    //     <div classNameName="color-priceD">
    //     <span classNameName="shoe_nameD">{productName}</span>
    //         <div classNameName="priceD">
    //             <span classNameName="price_numD">${productPrice}</span>
    //             {/* <span classNameName="price_letter">Nine dollar only</span> */}
    //         </div>
    //         </div>
    //     </div>
    //     <div classNameName="buttonD">
    //         <div classNameName="button-layerD"></div>
    //         <button>Add To Cart</button>
    //     </div>
        
    // </div>
               
    //     </div>
    //     </div>
    <div className="container">
 
  
  <div className="left-column">
  <Link to={`/product/${productName}`}> <img  src={productImage} alt=""/></Link>
    
  </div>
 
 
  
  <div className="right-column">
 
    
    <div className="product-description">
      <span>Headphones</span>
      <h1>{productName}</h1>
      <p>The preferred choice of a vast range of acclaimed DJs. Punchy, bass-focused sound and high isolation. Sturdy headband and on-ear cushions suitable for live performance</p>
    </div>
 
    
    <div className="product-configuration">
 
      
      <div className="product-color">
        <span>Color</span>
 
        <div className="color-choose">
          <div>
            <input data-image="red" type="radio" id="red" name="color" value="red" checked />
            <label for="red"><span></span></label>
          </div>
          <div>
            <input data-image="blue" type="radio" id="blue" name="color" value="blue" />
            <label for="blue"><span></span></label>
          </div>
          <div>
            <input data-image="black" type="radio" id="black" name="color" value="black" />
            <label for="black"><span></span></label>
          </div>
        </div>
 
      </div>
 
      
      <div className="cable-config">
        <span>Cable configuration</span>
 
        <div className="cable-choose">
          <button>Straight</button>
          <button>Coiled</button>
          <button>Long-coiled</button>
        </div>
 
        
      </div>
    </div>
 
    
    <div className="product-price">
      <span>{productPrice}$</span>
      <a href="#" className="cart-btn">Add to cart</a>
    </div>
  </div>
</div>
    )
}
