import "./css/landingStyle.css"
import { Link } from 'react-router-dom'
import React, { useState, useEffect } from 'react';
import image1 from './switchimages/neun-vorteile-von-second-hand-kleidungnikin-eu.jpg'
import image2 from './switchimages/pexels-pixabay-325876.jpg'
import image3 from './switchimages/AdobeStock_119888051.jpg'
import image4 from './switchimages/F9tjZiPUEm-kkcdX20ZU5R_YpNBlnBeQvXfOphczskE.jpg'
export default function LandingPage() {
  const [backgroundIndex, setBackgroundIndex] = useState(0);
  const backgroundImageUrls = [
    image1,
    image2,
    image3,
    image4
    
  ];
  useEffect(() => {
    const interval = setInterval(() => {
      setBackgroundIndex((prevIndex) => (prevIndex + 1) % backgroundImageUrls.length);
    }, 3000);

    return () => clearInterval(interval);
  }, [backgroundImageUrls.length]);

  const backgroundStyle = {
    backgroundImage: backgroundImageUrls[backgroundIndex],
  };
  return (<div className="boddy" style={backgroundStyle}>
    <div className='center'>
<Link className="buttonG" to="/Test"><span>Get Started</span></Link> 
</div>
</div>
  )
}
